import axios from 'axios';
export default {
  data: () => ({
    search: '',
    dialog: false,
    dialogDelete: false,
    headers: [{
      title: '#',
      align: 'start',
      sortable: false,
      key: 'UserId'
    }, {
      title: 'First Name',
      key: 'FirstName'
    }, {
      title: 'Middle Name',
      key: 'MiddleName'
    }, {
      title: 'Last Name',
      key: 'LastName'
    }, {
      title: 'Address',
      key: 'Address'
    }, {
      title: 'Contact',
      key: 'ContactNumber'
    }, {
      title: 'Role',
      key: 'Role'
    }, {
      title: 'Actions',
      key: 'actions',
      sortable: false
    }],
    users: [],
    editedIndex: -1,
    editedItem: {
      UserId: '',
      FirstName: '',
      MiddleName: '',
      LastName: '',
      Email: '',
      Password: '',
      Address: '',
      Contact: '',
      Role: ''
    },
    defaultItem: {
      UserId: '',
      FirstName: '',
      MiddleName: '',
      LastName: '',
      Email: '',
      Password: '',
      Address: '',
      Contact: '',
      Role: ''
    }
  }),
  computed: {
    formTitle() {
      return this.editedIndex === -1 ? 'New Item' : 'Edit Item';
    }
  },
  watch: {
    dialog(val) {
      val || this.close();
    },
    dialogDelete(val) {
      val || this.closeDelete();
    }
  },
  created() {
    this.initialize();
  },
  methods: {
    async initialize() {
      try {
        const respond = await axios.get('agentData');
        this.users = respond.data;
      } catch (error) {
        console.log(error);
      }
    },
    editItem(item) {
      this.editedIndex = this.users.indexOf(item);
      this.editedItem = Object.assign({}, item);
      this.dialog = true;
    },
    deleteItem(item) {
      this.editedIndex = this.users.indexOf(item);
      this.editedItem = Object.assign({}, item);
      this.dialogDelete = true;
    },
    async deleteItemConfirm() {
      const response = await axios.post('deleteUser', this.editedItem);
      console.log(response);
      this.initialize();
      this.closeDelete();
    },
    close() {
      this.dialog = false;
      this.$nextTick(() => {
        this.editedItem = Object.assign({}, this.defaultItem);
        this.editedIndex = -1;
      });
    },
    closeDelete() {
      this.dialogDelete = false;
      this.$nextTick(() => {
        this.editedItem = Object.assign({}, this.defaultItem);
        this.editedIndex = -1;
      });
    },
    async save() {
      if (this.editedIndex > -1) {
        Object.assign(this.users[this.editedIndex], this.editedItem);
      } else {
        const response = await axios.post('agentregister', this.editedItem);
        console.log(response.data);
      }
      this.initialize();
      this.close();
    }
  }
};
